import React, { useState } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';

const App = () => {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState([]);
  const [sessionId] = useState(uuidv4());
  const [chatOpen, setChatOpen] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      const res = await fetch('http://localhost:4000/api/product-information', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setResponse((prev) => [...prev, { sender: 'user', text: question }, { sender: 'bot', text: data.response }]);
      setQuestion('');
    } else {
      setResponse((prev) => [...prev, { sender: 'bot', text: 'Please enter a question.' }]);
    }
  };

  const handleFollowUp = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      const res = await fetch('http://localhost:4000/api/follow-up', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setResponse((prev) => [...prev, { sender: 'user', text: question }, { sender: 'bot', text: data.response }]);
      setQuestion('');
    } else {
      setResponse((prev) => [...prev, { sender: 'bot', text: 'Please enter a follow-up question.' }]);
    }
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  const closeChat = () => {
    setChatOpen(false);
  };

  return (
    <div className="App">
      <div className={`chat-bubble ${chatOpen ? 'open' : ''}`} onClick={toggleChat}>
        <div className="bubble-icon">ðŸ’¬</div>
      </div>
      {chatOpen && (
        <div className="chat-window">
          <div className="chat-header">
            <span>Chat with Us</span>
            <button className="close-button" onClick={closeChat}>x</button>
          </div>
          <div className="chat-body">
            {response.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <form onSubmit={handleSubmit} className="chat-form">
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Type your message"
              className="chat-input"
            />
            <button type="submit" className="chat-submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
