import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import { Button, TextField, IconButton, Paper, Typography } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';

const App = () => {
  const [question, setQuestion] = useState('');
  const [sessionId, setSessionId] = useState(uuidv4());
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [orderNumber, setOrderNumber] = useState('');
  const [issueDescription, setIssueDescription] = useState('');
  const [photo, setPhoto] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [showTicketForm, setShowTicketForm] = useState(false);
  const [showAdditionalForm, setShowAdditionalForm] = useState(false);
  const chatWindowRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (chatOpen && messages.length === 0) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { user: '', bot: 'Hello, welcome to our furniture store. My name is Eve and I am your customer representative. Please let me know how I can help you?' },
        { user: '', bot: '', options: ['Product Information', 'Submit a Ticket for Damaged Product', 'Delivery Update', 'Place an Order'] }
      ]);
    }
  }, [chatOpen, messages.length]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleOptionClick = async (option) => {
    console.log('Option selected:', option);
    setMessages((prevMessages) => [...prevMessages, { user: option, bot: '' }]);
    if (option === 'Product Information') {
      console.log('Sending initial product information request with sessionId:', sessionId);
      const res = await fetch('http://localhost:4000/api/product-information', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ sessionId }),
      });

      const data = await res.json();
      console.log('Product Information API response:', data);
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
    } else if (option === 'Submit a Ticket for Damaged Product') {
      setMessages((prevMessages) => [
        ...prevMessages,
        { user: '', bot: 'Please provide your order number, a description of the issue, and a photo of the damage.' }
      ]);
      setShowTicketForm(true);
    } else {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: `Option "${option}" selected. Feature to be implemented.` }]);
    }
  };

  const handleTicketSubmission = async (e) => {
    e.preventDefault();
    console.log('Submitting ticket with sessionId:', sessionId);

    const formData = new FormData();
    formData.append('orderNumber', orderNumber);
    formData.append('issueDescription', issueDescription);
    formData.append('sessionId', sessionId);
    if (photo) {
      formData.append('photo', photo);
    }

    const res = await fetch('http://localhost:4000/api/submit-ticket', {
      method: 'POST',
      body: formData,
    });

    const data = await res.json();
    console.log('Submit Ticket API response:', data);
    if (data.error) {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.error }]);
    } else {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
      setShowTicketForm(false);
      setShowAdditionalForm(true);
    }

    setOrderNumber('');
    setIssueDescription('');
    setPhoto(null);
    setPhotoPreview(null);
  };

  const handleAdditionalInfoSubmission = async (e) => {
    e.preventDefault();
    console.log('Submitting additional info with sessionId:', sessionId);

    const formData = new FormData();
    formData.append('sessionId', sessionId);
    formData.append('answer', question);
    if (photo) {
      formData.append('additionalPhoto', photo);
    }

    const res = await fetch('http://localhost:4000/api/submit-additional-info', {
      method: 'POST',
      body: formData,
    });

    const data = await res.json();
    console.log('Submit Additional Info API response:', data);
    if (data.error) {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.error }]);
    } else {
      setMessages((prevMessages) => [...prevMessages, { user: question, bot: data.response }]);
      setShowAdditionalForm(true);
      setQuestion('');
      setPhoto(null);
      setPhotoPreview(null);
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
    setPhotoPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting follow-up question with sessionId:', sessionId, 'and question:', question);

    if (question.trim()) {
      const userMessage = { user: question, bot: '' };
      setMessages((prevMessages) => [...prevMessages, userMessage]);
      setQuestion('');

      const formData = new FormData();
      formData.append('question', question);
      formData.append('sessionId', sessionId);
      if (photo) {
        formData.append('additionalPhoto', photo);
      }

      console.log('Form data for follow-up:', { question, sessionId });

      const res = await fetch('http://localhost:4000/api/product-information-followup', {
        method: 'POST',
        body: formData,
      });

      const data = await res.json();
      console.log('Product Information Follow-up API response:', data);
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
      setPhoto(null);
      setPhotoPreview(null);
    } else {
      alert('Please enter a question.');
    }
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  const handleEndSession = () => {
    setSessionId(uuidv4());
    setMessages([]);
    setChatOpen(false);
  };

  const handleClickOutside = (e) => {
    if (chatWindowRef.current && !chatWindowRef.current.contains(e.target)) {
      setChatOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="App">
      <div className={`chat-bubble ${chatOpen ? 'open' : ''}`} onClick={toggleChat}>
        <span role="img" aria-label="chat icon" className="bubble-icon">ðŸ’¬</span>
      </div>
      {chatOpen && (
        <Paper elevation={3} className="chat-window" ref={chatWindowRef}>
          <div className="chat-header">
            <Typography variant="h6">Furniture Store Assistant</Typography>
            <IconButton onClick={toggleChat} size="small">
              <CloseIcon style={{ color: 'white' }} />
            </IconButton>
          </div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div key={index} className="chat-message">
                {msg.user && <div className="user-message">{msg.user}</div>}
                {msg.bot && <div className="bot-response">{msg.bot}</div>}
                {msg.options && (
                  <div className="options">
                    {msg.options.map((option, i) => (
                      <Button key={i} variant="contained" onClick={() => handleOptionClick(option)} className="option-button">
                        {option}
                      </Button>
                    ))}
                  </div>
                )}
                {msg.photo && <img src={msg.photo} alt="Uploaded" className="chat-photo" />}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          {showTicketForm && (
            <form onSubmit={handleTicketSubmission} className="chat-form">
              <TextField
                variant="outlined"
                fullWidth
                value={orderNumber}
                onChange={(e) => setOrderNumber(e.target.value)}
                placeholder="Order Number"
                className="chat-input"
                required
              />
              <TextField
                variant="outlined"
                fullWidth
                multiline
                rows={4}
                value={issueDescription}
                onChange={(e) => setIssueDescription(e.target.value)}
                placeholder="Issue Description"
                className="chat-input"
                required
              />
              <input
                type="file"
                onChange={handlePhotoChange}
                className="chat-input"
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <Button type="submit" variant="contained" color="primary" className="chat-submit" endIcon={<SendIcon />}>
                Submit Ticket
              </Button>
            </form>
          )}
          {showAdditionalForm && (
            <form onSubmit={handleAdditionalInfoSubmission} className="chat-form">
              <TextField
                variant="outlined"
                fullWidth
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Type your answer here"
                className="chat-input"
              />
              <input
                type="file"
                name="additionalPhoto"
                onChange={handlePhotoChange}
                className="chat-input"
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <Button type="submit" variant="contained" color="primary" className="chat-submit" endIcon={<SendIcon />}>
                Send
              </Button>
            </form>
          )}
          {!showTicketForm && !showAdditionalForm && (
            <form onSubmit={handleSubmit} className="chat-form">
              <TextField
                variant="outlined"
                fullWidth
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Type your message"
                className="chat-input"
              />
              <input
                type="file"
                onChange={handlePhotoChange}
                className="chat-input"
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <Button type="submit" variant="contained" color="primary" className="chat-submit" endIcon={<SendIcon />}>
                Send
              </Button>
            </form>
          )}
          <Button onClick={handleEndSession} variant="contained" color="secondary" className="end-session-btn">
            Close Session
          </Button>
        </Paper>
      )}
    </div>
  );
};

export default App;
