import React, { useState } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';

const App = () => {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');
  const [sessionId] = useState(uuidv4());

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      const res = await fetch('http://localhost:4000/api/product-information', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setResponse(data.response);
      setQuestion('');
    } else {
      setResponse('Please enter a question.');
    }
  };

  const handleFollowUp = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      const res = await fetch('http://localhost:4000/api/follow-up', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setResponse(data.response);
      setQuestion('');
    } else {
      setResponse('Please enter a follow-up question.');
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question"
        />
        <button type="submit">Send</button>
      </form>
      <form onSubmit={handleFollowUp}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a follow-up question"
        />
        <button type="submit">Send Follow-Up</button>
      </form>
      <div>
        <h3>Response:</h3>
        <p>{response}</p>
      </div>
    </div>
  );
};

export default App;
