import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const App = () => {
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatOpen) {
      const welcomeMessage = {
        user: '',
        bot: 'Hello, welcome to our furniture store. My name is Eve and I am your customer representative. Please let me know how I can help you?',
        options: ['Product Information', 'Submit a Ticket for Damaged Product', 'Delivery Update', 'Place an Order']
      };
      setMessages([welcomeMessage]);
    }
  }, [chatOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:4000/api/query', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ question }),
    });

    const data = await res.json();
    const newMessage = { user: question, bot: data.choices[0].message.content };
    setMessages([...messages, newMessage]);
    setQuestion('');
  };

  const handleOptionClick = async (option) => {
    const res = await fetch('http://localhost:4000/api/query', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ question: option }),
    });

    const data = await res.json();
    const newMessage = { user: option, bot: data.choices[0].message.content };
    setMessages([...messages, newMessage]);
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  const handleClickOutside = (e) => {
    if (chatWindowRef.current && !chatWindowRef.current.contains(e.target)) {
      setChatOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="App">
      <div className={`chat-bubble ${chatOpen ? 'open' : ''}`} onClick={toggleChat}>
        <div className="bubble-icon">ðŸ’¬</div>
      </div>
      {chatOpen && (
        <div className="chat-window" ref={chatWindowRef}>
          <div className="chat-header">Furniture Store Assistant</div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div key={index} className="chat-message">
                {msg.user && <div className="user-message">{msg.user}</div>}
                <div className="bot-response">
                  {msg.bot}
                  {msg.options && (
                    <div className="options">
                      {msg.options.map((option, i) => (
                        <button key={i} onClick={() => handleOptionClick(option)} className="option-button">
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
          <form onSubmit={handleSubmit} className="chat-form">
            <input 
              type="text" 
              value={question} 
              onChange={(e) => setQuestion(e.target.value)} 
              placeholder="Ask a question" 
              className="chat-input"
            />
            <button type="submit" className="chat-submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
