import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    let endpoint;
    let productName;

    if (question.toLowerCase().includes('product information for')) {
      endpoint = 'product-information';
      productName = question.replace(/.*product information for/i, '').trim();
    }

    if (endpoint && productName) {
      const res = await fetch(`http://localhost:4000/api/${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ productName }),
      });

      const data = await res.json();
      setResponse(data.response);
      setQuestion('');
    } else {
      setResponse('Please specify a valid question.');
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question"
        />
        <button type="submit">Send</button>
      </form>
      <div>
        <h3>Response:</h3>
        <p>{response}</p>
      </div>
    </div>
  );
};

export default App;
