import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';

const App = () => {
  const [question, setQuestion] = useState('');
  const [sessionId, setSessionId] = useState(uuidv4());
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showYesNoButtons, setShowYesNoButtons] = useState(false);
  const messagesEndRef = useRef(null);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatOpen && messages.length === 0) {
      setMessages([
        { user: '', bot: 'Hello, welcome to our furniture store. My name is Eve and I am your customer representative. Please let me know how I can help you?' },
        { user: '', bot: '', options: ['Product Information', 'Submit a Ticket for Damaged Product', 'Delivery Update', 'Place an Order'] }
      ]);
    }
  }, [chatOpen]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleOptionClick = async (option) => {
    setMessages((prevMessages) => [...prevMessages, { user: option, bot: '' }]);
    if (option === 'Product Information') {
      const res = await fetch('http://localhost:4000/api/product-information', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ sessionId }),
      });

      const data = await res.json();
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
    } else if (option === 'Submit a Ticket for Damaged Product') {
      setMessages((prevMessages) => [
        ...prevMessages,
        { user: '', bot: 'Please provide your order number, a description of the issue, and a photo of the damage.' }
      ]);
    } else {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: `Option "${option}" selected. Feature to be implemented.` }]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      const userMessage = { user: question, bot: '' };
      setMessages((prevMessages) => [...prevMessages, userMessage]);
      setQuestion('');

      const res = await fetch('http://localhost:4000/api/product-information-followup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }), // Ensure sessionId is passed
      });

      const data = await res.json();
      if (data.response.includes('Was the packaging damaged from the outside?') || data.response.includes('Do you have the original packaging and the box?')) {
        setShowYesNoButtons(true);
      } else {
        setShowYesNoButtons(false);
      }
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
    } else {
      alert('Please enter a question.');
    }
  };

  const handleYesNoClick = async (answer) => {
    const userMessage = { user: answer, bot: '' };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    const res = await fetch('http://localhost:4000/api/product-information-followup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ question: answer, sessionId }), // Ensure sessionId is passed
    });

    const data = await res.json();
    setShowYesNoButtons(false);
    setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  const handleEndSession = () => {
    setSessionId(uuidv4());
    setMessages([]);
    setChatOpen(false);
    setQuestion('');
  };

  const handleClickOutside = (e) => {
    if (chatWindowRef.current && !chatWindowRef.current.contains(e.target)) {
      setChatOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="App">
      <div className={`chat-bubble ${chatOpen ? 'open' : ''}`} onClick={toggleChat}>
        <span role="img" aria-label="chat icon" className="bubble-icon">ðŸ’¬</span>
      </div>
      {chatOpen && (
        <div className="chat-window" ref={chatWindowRef}>
          <div className="chat-header">
            <span>Furniture Store Assistant</span>
            <button className="close-btn" onClick={toggleChat}>Ã—</button>
          </div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div key={index} className="chat-message">
                {msg.user && <div className="user-message">{msg.user}</div>}
                {msg.bot && <div className="bot-response">{msg.bot}</div>}
                {msg.options && (
                  <div className="options">
                    {msg.options.map((option, i) => (
                      <button key={i} onClick={() => handleOptionClick(option)} className="option-button">
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          {showYesNoButtons ? (
            <div className="yes-no-buttons">
              <button onClick={() => handleYesNoClick('Yes')} className="yes-no-button">Yes</button>
              <button onClick={() => handleYesNoClick('No')} className="yes-no-button">No</button>
            </div>
          ) : (
            <form onSubmit={handleSubmit} className="chat-form">
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Type your message"
                className="chat-input"
              />
              <button type="submit" className="chat-submit">Send</button>
            </form>
          )}
          <button onClick={handleEndSession} className="end-session-btn">Close Session</button>
        </div>
      )}
    </div>
  );
};

export default App;
