import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';

const App = () => {
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState(uuidv4());
  const [showTicketForm, setShowTicketForm] = useState(false);
  const [orderNumber, setOrderNumber] = useState('');
  const [issueDescription, setIssueDescription] = useState('');
  const [photo, setPhoto] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [showAdditionalForm, setShowAdditionalForm] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setMessages((prevMessages) => [
      ...prevMessages,
      { user: '', bot: 'Hello, welcome to our furniture store. My name is Eve and I am your customer representative. Please let me know how I can help you?' },
      { user: '', bot: '', options: ['Product Information', 'Submit a Ticket for Damaged Product', 'Delivery Update', 'Place an Order'] }
    ]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleOptionClick = async (option) => {
    setMessages((prevMessages) => [...prevMessages, { user: option, bot: '' }]);
    if (option === 'Product Information') {
      try {
        const res = await axios.post('http://localhost:4000/api/product-information', { sessionId });
        setMessages((prevMessages) => [...prevMessages, { user: '', bot: res.data.response }]);
      } catch (err) {
        console.error(err);
      }
    } else if (option === 'Submit a Ticket for Damaged Product') {
      setShowTicketForm(true);
    } else {
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: `Option "${option}" selected. Feature to be implemented.` }]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (question.trim()) {
      setMessages((prevMessages) => [...prevMessages, { user: question, bot: '' }]);
      setQuestion('');
      try {
        const res = await axios.post('http://localhost:4000/api/product-information-followup', { question, sessionId });
        setMessages((prevMessages) => [...prevMessages, { user: '', bot: res.data.response }]);
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleTicketSubmission = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('orderNumber', orderNumber);
    formData.append('issueDescription', issueDescription);
    formData.append('sessionId', sessionId);
    if (photo) {
      formData.append('photo', photo);
    }
    try {
      const res = await axios.post('http://localhost:4000/api/submit-ticket', formData);
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: res.data.response }]);
      setShowTicketForm(false);
      setShowAdditionalForm(true);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAdditionalInfoSubmission = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('sessionId', sessionId);
    formData.append('answer', question);
    if (photo) {
      formData.append('additionalPhoto', photo);
    }
    try {
      const res = await axios.post('http://localhost:4000/api/submit-additional-info', formData);
      setMessages((prevMessages) => [...prevMessages, { user: question, bot: res.data.response }]);
      setQuestion('');
      setPhoto(null);
      setPhotoPreview(null);
    } catch (err) {
      console.error(err);
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
    setPhotoPreview(URL.createObjectURL(file));
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-header">
          <h2>Furniture Store Assistant</h2>
        </div>
        <div className="chat-body">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              {msg.user && <div className="user-message">{msg.user}</div>}
              {msg.bot && <div className="bot-message">{msg.bot}</div>}
              {msg.options && (
                <div className="options">
                  {msg.options.map((option, i) => (
                    <button key={i} onClick={() => handleOptionClick(option)} className="option-button">{option}</button>
                  ))}
                </div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div className="chat-footer">
          {showTicketForm && (
            <form onSubmit={handleTicketSubmission} className="ticket-form">
              <input
                type="text"
                value={orderNumber}
                onChange={(e) => setOrderNumber(e.target.value)}
                placeholder="Order Number"
                required
              />
              <textarea
                value={issueDescription}
                onChange={(e) => setIssueDescription(e.target.value)}
                placeholder="Issue Description"
                required
              />
              <input
                type="file"
                onChange={handlePhotoChange}
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <button type="submit">Submit Ticket</button>
            </form>
          )}
          {showAdditionalForm && (
            <form onSubmit={handleAdditionalInfoSubmission} className="additional-info-form">
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Type your answer here"
                required
              />
              <input
                type="file"
                onChange={handlePhotoChange}
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <button type="submit">Submit</button>
            </form>
          )}
          {!showTicketForm && !showAdditionalForm && (
            <form onSubmit={handleSubmit} className="chat-form">
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Type your message"
                required
              />
              <input
                type="file"
                onChange={handlePhotoChange}
                accept="image/*"
              />
              {photoPreview && <img src={photoPreview} alt="Preview" className="photo-preview" />}
              <button type="submit">Send</button>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
