import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';

const App = () => {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');
  const [sessionId] = useState(uuidv4());
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatOpen) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { user: '', bot: 'Hello, welcome to our furniture store. My name is Eve and I am your customer representative. Please let me know how I can help you?' },
        { user: '', bot: 'Options: Product Information, Submit a Ticket for Damaged Product, Delivery Update, Place an Order' }
      ]);
    }
  }, [chatOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      setMessages((prevMessages) => [...prevMessages, { user: question, bot: '' }]);
      const res = await fetch('http://localhost:4000/api/product-information', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
      setQuestion('');
    } else {
      setResponse('Please enter a question.');
    }
  };

  const handleFollowUp = async (e) => {
    e.preventDefault();

    if (question.trim()) {
      setMessages((prevMessages) => [...prevMessages, { user: question, bot: '' }]);
      const res = await fetch('http://localhost:4000/api/follow-up', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, sessionId }),
      });

      const data = await res.json();
      setMessages((prevMessages) => [...prevMessages, { user: '', bot: data.response }]);
      setQuestion('');
    } else {
      setResponse('Please enter a follow-up question.');
    }
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  const handleClickOutside = (e) => {
    if (chatWindowRef.current && !chatWindowRef.current.contains(e.target)) {
      setChatOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="App">
      <div className={`chat-bubble ${chatOpen ? 'open' : ''}`} onClick={toggleChat}>
        <div className="bubble-icon">ðŸ’¬</div>
      </div>
      {chatOpen && (
        <div className="chat-window" ref={chatWindowRef}>
          <div className="chat-header">
            <span>Furniture Store Assistant</span>
            <button className="close-btn" onClick={toggleChat}>Ã—</button>
          </div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div key={index} className="chat-message">
                {msg.user && <div className="user-message">{msg.user}</div>}
                {msg.bot && <div className="bot-response">{msg.bot}</div>}
              </div>
            ))}
          </div>
          <form onSubmit={handleSubmit} className="chat-form">
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Type your message"
              className="chat-input"
            />
            <button type="submit" className="chat-submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
