import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    let endpoint;
    let payload;

    if (question.toLowerCase().includes('product information for')) {
      endpoint = 'product-information';
      payload = { productName: question.replace(/.*product information for/i, '').trim() };
    } else if (question.toLowerCase().includes('submit a ticket')) {
      endpoint = 'submit-ticket';
      payload = {
        customer_name: 'John Doe', // Example values, replace with actual user input
        customer_email: 'john.doe@example.com',
        order_number: '12345',
        product_name: 'Kumasi Smoke RAF Sectional',
        delivery_date: '2023-01-01',
        damage_description: 'The product was damaged.',
        photo_url: 'http://example.com/photo.jpg'
      };
    } else if (question.toLowerCase().includes('delivery update for order')) {
      endpoint = 'delivery-update';
      payload = { orderId: question.replace(/.*delivery update for order/i, '').trim() };
    } else if (question.toLowerCase().includes('place an order')) {
      endpoint = 'place-order';
      payload = {
        customer_name: 'Jane Doe', // Example values, replace with actual user input
        customer_email: 'jane.doe@example.com',
        product_name: 'Kumasi Smoke RAF Sectional',
        quantity: 1,
        price: 999.99,
        delivery_date: '2023-01-01'
      };
    }

    if (endpoint && payload) {
      const res = await fetch(`http://localhost:4000/api/${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      setResponse(data.response);
      setQuestion('');
    } else {
      setResponse('Please specify a valid question.');
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question"
        />
        <button type="submit">Send</button>
      </form>
      <div>
        <h3>Response:</h3>
        <p>{response}</p>
      </div>
    </div>
  );
};

export default App;
